-- customerscreate database customer_behaviour;
-- create database customer_behaviour;
show databases;
use customer_behaviour;
describe customer;
select * from customer limit 20;
 -- What is the total revenue generated by male vs female customers?
select gender , sum(purchase_amount) as revenue 
from customer 
group by gender;
 -- Which customer used discount but still spent more than average purchase amount
 select customer_id , purchase_amount
 from customer 
 where discount_applied='Yes' and purchase_amount >= (select avg(purchase_amount) from customer);
 
 -- Which are the top 5 products with highest average review rating?
 select item_purchased, round(avg(review_rating),2) as avg_product_rating
 from customer 
 group by item_purchased
 order by avg_product_rating desc limit 5;
 
-- Compare the average purchase amount between standard and express shipping
select shipping_type, round(avg(purchase_amount),2)
from customer 
where shipping_type in ('Standard', 'Express')
group by shipping_type;

-- Do subscribed customers spend more ? compare average spend and total revenue 
-- between subscribers and non-subscribers
select subscription_status, count(customer_id) as total_customers, avg(purchase_amount) as avg_spent,
round(sum(purchase_amount),2) as total_revenue
from customer
group by subscription_status
order by total_revenue , avg_spent desc;

-- which 5 products have the highest percentage of purchases with discount applied?
 select item_purchased , round(sum(discount_applied ='Yes') /count(*) * 100 , 2) as discount_rate
 from customer
 group by item_purchased
 order by discount_rate desc
 limit 5;
 
 -- Segment customers into New, Returning and Loyal based on their total number of previous purchases and show the
--  count of each segment
with customer_type as (select customer_id, previous_purchases,
case 
	when previous_purchases = 1  then 'New'
    when previous_purchases between 2 and 10 then 'Returning'
    else 'Loyal'
    end as Customer_segment
    from customer
)
select customer_segment, count(*) as "Number of customers"
from customer_type
group by customer_segment;

-- What are the top 3 most purchased products within each category
with item_counts as (
select category , item_purchased, count(customer_id) as total_orders,
row_number() over(partition by category order by count(customer_id) desc ) as item_rank
from customer
group by category , item_purchased
)
select item_rank, category, item_purchased, total_orders
from item_counts
where item_rank<=3;

-- Are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?
select subscription_status , count(customer_id) as repeat_buyers
from customer
where previous_purchases >5
group by subscription_status; 

-- What is the revenue contribution of each age-group
select age_group, sum(purchase_amount) as total_revenue
from customercustomer
group by age_group order by total_revenue desc;
drop database customer_behaviour;